mapper.select:
SELECT /* UserMngDao.searchManagers */
       A.*
FROM   manager A


    AND UPPER(A.login_id) LIKE UPPER(#{loginId}) || '%'


    AND UPPER(A.mgr_name) LIKE UPPER(#{mgrName}) || '%'


    AND A.role = #{role}


    AND A.disabled_yn = #{disabledYn}


    AND A.locked_yn = #{lockedYn}


ORDER BY A.id DESC

mapper.delete:
DELETE /* UserMngDao.removeManagerById */
  FROM manager
 WHERE id = #{id}
