mapper.select:
 SELECT /* 추적정보 목록 조회 selectTraceListByIpAndTime */
      *
 FROM ns_trace_api
WHERE 1=1

  AND remote_addr = #{remoteAddr}
  AND access_time > #{criteriaTime}

mapper.insert:
MERGE /* 추적정보 저장 saveTrace */
INTO ns_trace_api T
USING DUAL ON (
  T.id = #{tokenId}
)
WHEN MATCHED THEN
  UPDATE SET
    remote_addr = #{remoteAddr},
    access_time = #{currentTime}
WHEN NOT MATCHED THEN
  INSERT (
    id,
    remote_addr,
    access_time
  ) VALUES (
    #{id},
    #{remoteAddr},
    #{currentTime}
  )
